Bash(/Users/max.saltonstall/jottogame/frontend/src/App.css << 'EOF'

/* AI game component */
.play-ai {
  background: linear-gradient\(135deg, #667eea 0%, #764ba2 100%\);
  padding: 1.5rem;
  border-radius: 12px;
  box-shadow: 0 4px 6px rgba\(0, 0, 0, 0.1\);
}

.play-ai h2 {
  margin: 0 0 1rem 0;
  color: white;
  font-size: 1.5rem;
}

.play-ai form {
  display: flex;
  flex-direction: column;
  gap: 1rem;
}

.play-ai label {
  color: white;
  font-weight: 600;
}

.play-ai input {
  padding: 0.75rem;
  font-size: 1rem;
  border: 2px solid rgba\(255, 255, 255, 0.3\);
  border-radius: 8px;
  background: rgba\(255, 255, 255, 0.9\);
}

.play-ai input:focus {
  outline: none;
  border-color: white;
  background: white;
}

.play-ai button {
  background: white;
  color: #764ba2;
  border: none;
  padding: 0.75rem 1.5rem;
  font-size: 1rem;
  font-weight: 600;
  border-radius: 8px;
  cursor: pointer;
  transition: transform 0.2s;
}

.play-ai button:hover:not\(:disabled\) {
  transform: translateY\(-2px\);
  box-shadow: 0 4px 8px rgba\(0, 0, 0, 0.2\);
}

.play-ai button:disabled {
  opacity: 0.6;
  cursor: not-allowed;
}

.ai-description {
  margin-top: 0.5rem;
  font-size: 0.9rem;
  color: rgba\(255, 255, 255, 0.9\);
  font-style: italic;
}

/* AI opponent indicator */
.opponent-guesses h3 {
  display: flex;
  align-items: center;
  gap: 0.5rem;
}
EOF)
